import React, { useEffect, useRef } from "react";
import cytoscape from "cytoscape";
import fcose from "cytoscape-fcose";
import { GraphNode, GraphEdge } from "../../types";

cytoscape.use(fcose);

interface GraphVisualizationProps {
  nodes: GraphNode[];
  edges: GraphEdge[];
  onNodeSelect: (nodeId: string) => void;
  onNodeHover: (nodeId: string | null) => void;
}

const GraphVisualization: React.FC<GraphVisualizationProps> = ({
  nodes,
  edges,
  onNodeSelect,
  onNodeHover,
}) => {
  const cyRef = useRef<HTMLDivElement>(null);
  const cyInstance = useRef<cytoscape.Core | null>(null);
  const tooltipRef = useRef<HTMLDivElement | null>(null);

  const clusterColors: Record<string, string> = {
    "NLP": "#4A90E2",
    "Computer Vision": "#7CB342",
    "Machine Learning": "#FB8C00", 
    "Default": "#666666",
  };

  const getNodeColor = (cluster?: string) => {
    return cluster ? clusterColors[cluster] || clusterColors["Default"] : clusterColors["Default"];
  };

  useEffect(() => {
    if (!cyRef.current) return;

    cyInstance.current = cytoscape({
      container: cyRef.current,
      elements: [
        ...nodes.map(node => ({
          data: {
            id: node.id,
            title: node.data.title,
            year: node.data.year,
            cluster: node.data.cluster,
            confidence: node.data.confidence,
          },
        })),
        ...edges.map(edge => ({
          data: {
            id: edge.id,
            source: edge.source,
            target: edge.target,
            type: edge.type,
          },
        })),
      ],
      style: [
        {
          selector: "node",
          style: {
            "background-color": (ele: any) => getNodeColor(ele.data("cluster")),
            "border-color": "#fff",
            "border-width": 2,
            label: "data(year)",
            "text-valign": "center",
            "text-halign": "center",
            width: 60,
            height: 60,
            "font-size": "12px",
            "font-weight": "600",
            color: "white",
          },
        },
        {
          selector: "node:hover",
          style: {
            "border-width": 3,
            "border-color": "#FFD700",
          },
        },
        {
          selector: "edge[type = \"citation\"]",
          style: {
            "line-color": "#888",
            "target-arrow-color": "#888",
            "target-arrow-shape": "triangle",
            width: 2,
          },
        },
        {
          selector: "edge[type = \"beats\"]",
          style: {
            "line-color": "#E74C3C",
            "target-arrow-color": "#E74C3C",
            "target-arrow-shape": "triangle",
            width: 3,
            "line-style": "dashed",
          },
        },
      ],
      layout: {
        name: "fcose",
        animate: true,
        animationDuration: 1000,
        fit: true,
        padding: 50,
      },
      minZoom: 0.3,
      maxZoom: 3,
      userPanningEnabled: true,
      userZoomingEnabled: true,
    });

    cyInstance.current.on("tap", "node", (evt) => {
      onNodeSelect(evt.target.id());
    });

    cyInstance.current.on("mouseover", "node", (evt) => {
      onNodeHover(evt.target.id());
    });

    cyInstance.current.on("mouseout", "node", () => {
      onNodeHover(null);
    });

    return () => {
      if (cyInstance.current) {
        cyInstance.current.destroy();
      }
    };
  }, [nodes, edges, onNodeSelect, onNodeHover]);

  return (
    <div
      ref={cyRef}
      style={{
        width: "100%",
        height: "100%",
        border: "1px solid #e0e0e0",
        borderRadius: "12px",
        backgroundColor: "#fafafa",
        cursor: "grab",
        overflow: "hidden",
        boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)",
      }}
    />
  );
};

export default GraphVisualization;
